import React, {Component} from 'react';

export default class WeatherDisplay extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null
    };
  }

  componentDidMount() {
    const name = this.props.name;
    const apiKey = '7d7de66dd8d5ba712b4daa70787e46e2'
    const URL = "https://api.openweathermap.org/data/2.5/weather?q=" +
      name +
      "&lang=ru&units=metric&appid=" + apiKey;
    fetch(URL).then(res => res.json()).then(json => {
      this.setState({ weatherData: json });
    });
  }

  render() {
    const weatherData = this.state.weatherData;
    if (!weatherData)
    return (<div>Подождите, данные о погоде загружаются</div>);
    const weather = weatherData.weather[0];
    const iconUrl = "http://openweathermap.org/img/w/" + weather.icon + ".png";
    return (
      <div>
        <h1>
          {weather.main} in {weatherData.name}
          <img src={iconUrl} alt={weatherData.description} />
        </h1>
        <p>Current: {weatherData.main.temp}°С</p>
        <p>High: {weatherData.main.temp_max}°С</p>
        <p>Low: {weatherData.main.temp_min}°С</p>
        <p>Wind Speed: {weatherData.wind.speed} м/с</p>
      </div>
    );
  }
}
