import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import DrawCard from './DrawCard'

const useStyles = makeStyles(theme => ({
  root: {
    width: 345,
    marginBottom: "14%",
    borderRadius: 25,
    padding: 10
  },
  expand: {
    transform: 'rotate(0deg)',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
}));

export default function WCard(props) {
  const [weatherData, setWeatherData] = useState({
  "coord": {
    "lon": 0,
    "lat": 0
  },
  "weather": [
    {
      "id": 0,
      "main": "",
      "description": "",
      "icon": ""
    }
  ],
  "base": "",
  "main": {
    "temp": 0,
    "feels_like": 0,
    "temp_min": 0,
    "temp_max": 0,
    "pressure": 0,
    "humidity": 0
  },
  "visibility": 0,
  "wind": {
    "speed": 0,
    "deg": 0
  },
  "clouds": {
    "all": 0
  },
  "dt": 0,
  "sys": {
    "type": 0,
    "id": 0,
    "message": 0,
    "country": "",
    "sunrise": 0,
    "sunset": 0
  },
  "timezone": 0,
  "id": 0,
  "name": "",
  "cod": 0
});
  const [updateTime, setUpdateTime] = useState(null);
  const [anchorEl, setAnchorEl] = useState(null);
  const [expanded, setExpanded] = useState(false);

  const classes = useStyles();

  useEffect(() => {
    fetchWeather();
  }, []);

  async function fetchWeather() {
    const city = "Харьков";
    const url = "https://api.openweathermap.org/data/2.5/weather?q=" +
    city + "&lang=ru&units=metric&appid=5fc73283d7afc0b780eee68e8e3bb82b";
    const responce = await fetch(url);
    const data = await responce.json();
    console.log(data);
    setWeatherData(data);
    setUpdateTime(new Date().toLocaleTimeString());
  }

  const handleClick = event => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };
  const handleDelete = () => {
    alert();
    setAnchorEl(null);
  };
  const handleExpand = () => {
    setExpanded(!expanded);
  };

  return(
    console.log("fuck"),
    <Card className={classes.root}>
    <CardContent>
      <Typography variant="h5">
        {weatherData.name}
      </Typography>
      <div className="crutch">
        <Typography variant="h2">
          {weatherData.main.temp} °С
        </Typography>
        <img
          className="image"
          src={"http://openweathermap.org/img/w/" +
            weatherData.weather[0].icon + ".png"}
        />
      </div>
      <Typography>
        {weatherData.weather[0].description.charAt(0).toUpperCase()
          + weatherData.weather[0].description.substring(1)}
      </Typography>
      <Typography color="textSecondary">
        Время последнего обновления: {updateTime}
      </Typography>
    </CardContent>
    <Collapse in={expanded} timeout="auto" unmountOnExit>
      <CardContent>
      <Typography>
        Мин. t: {Math.round(weatherData.main.temp)} °С
      </Typography>
      <Typography>
        Макс. t: Math.round(props.max) °С
      </Typography>
      <Typography>
        Ощущается как: Math.round({weatherData.main.feels_like}) °С
      </Typography>
      <Typography>
        Скорость ветра: props.wind м/с
      </Typography>
      </CardContent>
    </Collapse>
      <CardActions>
      <IconButton
        className={clsx(classes.expand, {
          [classes.expandOpen]: expanded,
        })}
        onClick={handleExpand}
        aria-expanded={expanded}
        aria-label="Развернуть"
      >
        <ExpandMoreIcon />
      </IconButton>
        <IconButton onClick={handleClick}>
          <MoreHorizIcon />
        </IconButton>
        <Menu
          id="simple-menu"
          anchorEl={anchorEl}
          keepMounted
          open={Boolean(anchorEl)}
          onClose={handleClose}
          >
          <MenuItem onClick={() => {
            fetchWeather();
            setAnchorEl(null);
          }}>Обновить</MenuItem>
            <MenuItem onClick={handleClose}>
            Показать подробную информацию
            </MenuItem>
          <MenuItem onClick={handleDelete}><span style={{  color: "#f44336"  }}>Удалить</span></MenuItem>
        </Menu>
      </CardActions>

    </Card>
  );
}
