import React, {Component} from 'react';
import './index.css';
import WeatherCard from './WeatherCard';
import TextField from '@material-ui/core/TextField';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import Search from './Search';

export default class DisplayWeather extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cities: ['Харьков', 'Киев', 'Одесса', 'Львов'],
      buffer: "",
    };
  }

  render() {
    const list = this.state.cities.slice();
    return (
      <div>
      <Box
        display="flex"
        flexWrap="wrap"
        justifyContent="space-between"
        >
        {this.state.cities.map((value, index) => {
        return <WeatherCard key = {index} name={value} />
        })}
      </Box>
      <TextField
        id="outlined-basic"
        label="Поиск по городу"
        variant="filled"
        fullWidth
        onChange={(e) => this.setState({buffer: e.target.value})}
        marginBottom="5"
      />
      <Button
        variant="contained"
        color="primary"
        size="large"
        onClick={() => {
          list.push(this.state.buffer);
          this.setState({cities: list});
        }}
        >Добавить
      </Button>
      </div>
    );
  }
}
