import React, {Component} from 'react';
import Button from '@material-ui/core/Button';
import UpdateIcon from '@material-ui/icons/Update';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

import IconButton from '@material-ui/core/IconButton';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

import DrawCard from './DrawCard'

export default class WeatherCard extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null,
      updateTime: null,
    };
    this.getWeather = this.getWeather.bind(this);
    };

  componentDidMount() {
    this.getWeather();
  }

  getWeather() {
    const name = this.props.name;
    const apiKey = '7d7de66dd8d5ba712b4daa70787e46e2'
    const URL = "https://api.openweathermap.org/data/2.5/weather?q=" +
      name +
      "&lang=ru&units=metric&appid=" + apiKey;
    fetch(URL).then(res => res.json()).then(json => {
      this.setState({ weatherData: json });
    });
    this.state.updateTime = new Date();
  }

  render() {
    const weatherData = this.state.weatherData;

    if (!weatherData)
    return (<div>Подождите, данные о погоде загружаются</div>);

    const weather = weatherData.weather[0];
    const iconUrl = "http://openweathermap.org/img/w/" + weather.icon + ".png";

    return (
      <div>
        <Button
          variant="contained"
          color="primary"
          size="large"
          startIcon={<UpdateIcon />}
          onClick={this.getWeather}
          >Обновить
        </Button>

    <DrawCard
      name={weatherData.name}
      iconUrl={iconUrl}
      description={weatherData.description}
      temp={weatherData.main.temp}
      main={weather.main}
      time={this.state.updateTime.toLocaleTimeString()}
      min={weatherData.main.temp_min}
      max={weatherData.main.temp_max}
      wind={weatherData.wind.speed}
    />
      </div>
    );
  }
}
