import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import UpdateIcon from '@material-ui/icons/Update';
import Button from '@material-ui/core/Button';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import red from '@material-ui/core/colors/red';

const useStyles = makeStyles(theme => ({
  root: {
    width: 345,
    marginBottom: "14%",
    borderRadius: 25,
    padding: 10
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
}));

export default function DrawCard(props) {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = event => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <Card className={classes.root}>
    <CardContent>
      <Typography variant="h5">
        {props.name}
      </Typography>
      <div className="crutch">
        <Typography variant="h2">
          {Math.round(props.temp)} °С
          </Typography>
        <img
          className="image"
          src={props.iconUrl}
          alt={props.description}
        />
      </div>
      <Typography>
        {props.main}
      </Typography>
      <Typography color="textSecondary">
        Время последнего обновления: {props.time}
      </Typography>
    </CardContent>
    <Collapse in={expanded} timeout="auto" unmountOnExit>
      <CardContent>
      <Typography>
        Мин. t: {Math.round(props.min)} °С
      </Typography>
      <Typography>
        Макс. t: {Math.round(props.max)} °С
      </Typography>
      <Typography>
        Ощущается как: {Math.round(props.feelsLike)} °С
      </Typography>
      <Typography>
        Скорость ветра: {props.wind} м/с
      </Typography>
      </CardContent>
    </Collapse>
      <CardActions>

        <IconButton onClick={handleClick}>
          <MoreHorizIcon />
        </IconButton>
        <Menu
          id="simple-menu"
          anchorEl={anchorEl}
          keepMounted
          open={Boolean(anchorEl)}
          onClose={handleClose}
          >
          <MenuItem onClick={() => {
            props.updateF();
            setAnchorEl(null);
          }}>Обновить</MenuItem>
          <MenuItem onClick={handleClose}>Показать подробную информацию</MenuItem>
          <MenuItem onClick={handleClose}><span style={{  color: "#f44336"  }}>Удалить</span></MenuItem>
        </Menu>
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="Развернуть"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>

    </Card>
  );
}
