import React, { useState, useEffect } from 'react';
import WeatherCard from './WeatherCard';

export default function Weather() {
  const [cities, setCities] = useState(["Харьков", "Киев", "Львов"]);

  const add = (city) => {
    cities.push(city);
  }

  const remove = (city) => {
    const index = cities.indexOf(city);
    if (index > -1) {
      cities.splice(index, 1);
    }
  }


  return(
    add("Париж"),
    remove("Львов"),
    console.log({cities}),
    <div>{cities}</div>
  );
}
