import WeatherCard from './WeatherCard';

export default function Weather() {
  const [cities, setCities] = useState([<WeatherCard city = "Харьков" />, <WeatherCard city = "Киев" />, <WeatherCard city = "Львов" />]);

  const add = (city) => {
    cities.push(city);
  }

  const remove = (city) => {
    const index = cities.indexOf(city);
    if (index > -1) {
      cities.splice(index, 1);
    }
  }


  return(
    // add("Париж"),
    // remove("Львов"),
    <div>{cities}
    {cities[0]}
     </div>
  );
}
