import React, { useState, useEffect } from 'react';
import WeatherCard from './WeatherCard';

export default function Weather() {
  const [cities, setCities] = useState([]);
  const [citiesJS, setCitiesJS] = useState([]);

  useEffect(() => {
    cities.map((value, index) => {
    return <div>cities</div>
    })
  });

  const remove = (city) => {
    const index = cities.indexOf(city);
    if (index > -1) {
      cities.splice(index, 1);
      citiesJS.splice(index, 1);
    }
    console.log(cities)
  }

  const add = (city) => {
    setCities([ ... city, {city}]);
    citiesJS.push(<WeatherCard key = {JSON.stringify(city)} city = {city} remove = {remove}/>);
  }

  return(
    add("Харьков"),
    add("Киев"),
    add("Одесса"),
    add("Львов"),
    remove("Киев"),
    <div>
    {cities.map(city => (
      <li key = {JSON.stringify(city)}>{city}</li>
    )
    )}
    {citiesJS}
     </div>
  );
}
