import React, {Component} from 'react';
import './index.css';
import WeatherCard from './WeatherCard';
import TextField from '@material-ui/core/TextField';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';

import { NavLink } from "react-router-dom";

export default class DisplayWeather extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cities: [],
      buffer: null,
    };
  }

  componentDidMount() {
    const newList = [];
    for (var i = 0; i < localStorage.length; i++) {
      newList.push(localStorage.getItem(i));
    }
    this.setState({cities: newList});
  }

  remove(e) {
    var array = [...this.state.cities];
    var index = array.indexOf(e.target.value)
    if (index !== -1) {
      array.splice(index, 1);
      this.setState({cities: array});
    }
  }

  render() {
    const list = this.state.cities.slice();

    for (var i = 0; i < list.length; i++) {
      localStorage.setItem(i, list[i]);
    }


    return (
      <div>
      <Box
        display="flex"
        flexWrap="wrap"
        >
        {this.state.cities.map((value, index) => {
        return <Box key = {index} marginRight="50px"><WeatherCard key = {index} name={value} /></Box>
        })}
        <Card className="search-card">
          <CardContent>
          <TextField
            className="text-field"
            id="outlined-basic"
            label="Поиск по городу"
            variant="filled"
            width="500px"
            onChange={(e) => this.setState({buffer: e.target.value})}
            onKeyPress={(e) => {
              if (e.key === "Enter") {
                list.push(this.state.buffer);
                this.setState({cities: list});
                this.setState({buffer: null});
                e.target.value="";
              }
            }}
            />
            </CardContent>
            <CardActions>
            <Button
              variant="contained"
              color="primary"
              size="medium"
              startIcon={<AddIcon />}
              onClick={() => {
                list.push(this.state.buffer);
                this.setState({cities: list});
              }}
              >Добавить
            </Button>
            </CardActions>
        </Card>
        <Button
          variant="contained"
          color="primary"
          size="medium"
          startIcon={<AddIcon />}
          onClick={this.remove}
          >Добавить
        </Button>
      </Box>

      </div>
    );
  }
}
