import React, {Component} from 'react';
import Button from '@material-ui/core/Button';
import UpdateIcon from '@material-ui/icons/Update';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

import IconButton from '@material-ui/core/IconButton';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

export default class WeatherDisplay extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null,
      updateTime: null,
    };
    this.getWeather = this.getWeather.bind(this);

    const [expanded, setExpanded] = React.useState(false);

    const handleExpandClick = () => {
      setExpanded(!expanded);
    };
  }

  componentDidMount() {
    this.getWeather();
  }

  getWeather() {
    const name = this.props.name;
    const apiKey = '7d7de66dd8d5ba712b4daa70787e46e2'
    const URL = "https://api.openweathermap.org/data/2.5/weather?q=" +
      name +
      "&lang=ru&units=metric&appid=" + apiKey;
    fetch(URL).then(res => res.json()).then(json => {
      this.setState({ weatherData: json });
    });
    this.state.updateTime = new Date();
  }



  render() {
    const weatherData = this.state.weatherData;

    if (!weatherData)
    return (<div>Подождите, данные о погоде загружаются</div>);

    const weather = weatherData.weather[0];
    const iconUrl = "http://openweathermap.org/img/w/" + weather.icon + ".png";

    return (
      <div>
        <h1>{weatherData.name}</h1>
        <img src={iconUrl} alt={weatherData.description} />
        <h1>{weather.main}</h1>
        <h1>{weatherData.main.temp} °С</h1>
        <h2>min: {weatherData.main.temp_min} °С</h2>
        <h2>max: {weatherData.main.temp_max} °С</h2>
        <h2>wind: {weatherData.wind.speed} м/с</h2>
        <Button
          variant="contained"
          color="primary"
          size="large"
          startIcon={<UpdateIcon />}
          onClick={this.getWeather}
          >Обновить
        </Button>
        <p>Время последнего обновления: {this.state.updateTime.toLocaleTimeString()}</p>

    <Card variant="outlined">
      <CardContent>
        <Typography variant="h6">
          {weatherData.name}
        </Typography>
        <img src={iconUrl} alt={weatherData.description} />
        <Typography variant="h2">
          {weatherData.main.temp} °С
        </Typography>
        <Typography>
          {weather.main}
        </Typography>
        <Typography color="textSecondary">
          Время последнего обновления: {this.state.updateTime.toLocaleTimeString()}
        </Typography>
      </CardContent>
      <CardActions>
        <IconButton
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="Развернуть"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>

        <CardContent>
          <Typography paragraph>Method:</Typography>
          <Typography paragraph>
            Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
            minutes.
          </Typography>
          <Typography paragraph>
            Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high
            heat. Add chicken, shrimp and chorizo, and cook, stirring occasionally until lightly
            browned, 6 to 8 minutes. Transfer shrimp to a large plate and set aside, leaving chicken
            and chorizo in the pan. Add pimentón, bay leaves, garlic, tomatoes, onion, salt and
            pepper, and cook, stirring often until thickened and fragrant, about 10 minutes. Add
            saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
          </Typography>
          <Typography paragraph>
            Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook
            without stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat to
            medium-low, add reserved shrimp and mussels, tucking them down into the rice, and cook
            again without stirring, until mussels have opened and rice is just tender, 5 to 7
            minutes more. (Discard any mussels that don’t open.)
          </Typography>
          <Typography>
            Set aside off of the heat to let rest for 10 minutes, and then serve.
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
      </div>
    );
  }
}
