import React, {Component} from 'react';
import Button from '@material-ui/core/Button';
import UpdateIcon from '@material-ui/icons/Update';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

import IconButton from '@material-ui/core/IconButton';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

import { makeStyles } from '@material-ui/core/styles';
const useStyles = makeStyles(theme => ({
  root: {
    maxWidth: 500,
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
}));

export default class WeatherDisplay extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null,
      updateTime: null,
    };
    this.getWeather = this.getWeather.bind(this);
    const classes = useStyles();
    };

  componentDidMount() {
    this.getWeather();
  }

  getWeather() {
    const name = this.props.name;
    const apiKey = '7d7de66dd8d5ba712b4daa70787e46e2'
    const URL = "https://api.openweathermap.org/data/2.5/weather?q=" +
      name +
      "&lang=ru&units=metric&appid=" + apiKey;
    fetch(URL).then(res => res.json()).then(json => {
      this.setState({ weatherData: json });
    });
    this.state.updateTime = new Date();
  }

  render() {
    const weatherData = this.state.weatherData;

    if (!weatherData)
    return (<div>Подождите, данные о погоде загружаются</div>);

    const weather = weatherData.weather[0];
    const iconUrl = "http://openweathermap.org/img/w/" + weather.icon + ".png";

    return (
      <div>
        <h1>{weatherData.name}</h1>
        <img src={iconUrl} alt={weatherData.description} />
        <h1>{weather.main}</h1>
        <h1>{weatherData.main.temp} °С</h1>
        <h2>min: {weatherData.main.temp_min} °С</h2>
        <h2>max: {weatherData.main.temp_max} °С</h2>
        <h2>wind: {weatherData.wind.speed} м/с</h2>
        <Button
          variant="contained"
          color="primary"
          size="large"
          startIcon={<UpdateIcon />}
          onClick={this.getWeather}
          >Обновить
        </Button>
        <p>Время последнего обновления: {this.state.updateTime.toLocaleTimeString()}</p>

    <Card variant="outlined">
      <CardContent>
        <Typography variant="h6">
          {weatherData.name}
        </Typography>
        <img src={iconUrl} alt={weatherData.description} />
        <Typography variant="h2">
          {weatherData.main.temp} °С
        </Typography>
        <Typography>
          {weather.main}
        </Typography>
        <Typography color="textSecondary">
          Время последнего обновления: {this.state.updateTime.toLocaleTimeString()}
        </Typography>
      </CardContent>
      <CardActions>
        <IconButton
          aria-label="Развернуть"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
    </Card>
      </div>
    );
  }
}
