import React, {Component} from 'react';
import WeatherCard from './WeatherCard';
import TextField from '@material-ui/core/TextField';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      buffer: "",
    };
  }

  render() {
    return (
      <div>
        <TextField
          id="outlined-basic"
          label="Поиск по городу"
          variant="filled"
          fullWidth
          onChange={(e) => this.setState({buffer: e.target.value})}
        />
        <Collapse in={expanded} timeout="auto" unmountOnExit>
          <CardContent>
          <Typography>
            Мин. t: {Math.round(props.min)} °С
          </Typography>
          <Typography>
            Макс. t: {Math.round(props.max)} °С
          </Typography>
          <Typography>
            Ощущается как: {Math.round(props.feelsLike)} °С
          </Typography>
          <Typography>
            Скорость ветра: {props.wind} м/с
          </Typography>
          </CardContent>
        </Collapse>
      </div>
    );
  }
}
