import React, {Component} from 'react';
import './index.css';
import WeatherCard from './WeatherCard';
import TextField from '@material-ui/core/TextField';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';

import { NavLink } from "react-router-dom";

export default class DisplayWeather extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cities: ['Харьков'],
      buffer: null,
    };
  };

  componentDidMount() {
    const newList = [];
    for (var i = 0; i < localStorage.length; i++) {
      newList.push(localStorage.getItem(this.state.cities[i]));
    }
    this.setState({cities: localStorage.getItem('харьков')});
  }

  componentDidUpdate() {
    const list = this.state.cities;

    for (var i = 0; i < list.length; i++) {
      localStorage.setItem(i, list[i]);
    }
  }

  removeItems = () => {
    this.setState({cities: []});
    localStorage.clear();
  }

  removeItem = (index) => {
    const newList = this.state.cities.slice();
    newList.pop();
    localStorage.removeItem(localStorage.length - 1);
    this.setState({cities: newList});
  }

  render() {
    // const list = this.state.cities.slice();
    //
    // for (var i = 0; i < list.length; i++) {
    //   localStorage.setItem(JSON.stringify(list[i]), list[i]);
    // }


    return (
      <div>
      <Box
        display="flex"
        flexWrap="wrap"
        >
        {this.state.cities.map((value, index) => {
        return <Box key = {index} marginRight="50px"><WeatherCard key = {index} name={value} /></Box>
        })}
        <Card className="search-card">
          <CardContent>
          <TextField
            className="text-field"
            id="outlined-basic"
            label="Поиск по городу"
            variant="filled"
            width="500px"
            onChange={(e) => this.setState({buffer: e.target.value})}
            onKeyPress={(e) => {
              if (e.key === "Enter") {
                list.push(this.state.buffer);
                this.setState({cities: list});
                this.setState({buffer: null});
                e.target.value="";
              }
            }}
            />
            </CardContent>
            <CardActions>
            <Button
              variant="contained"
              color="primary"
              size="medium"
              startIcon={<AddIcon />}
              onClick={() => {
                list.push(this.state.buffer);
                this.setState({cities: list});
              }}
              >Добавить
            </Button>
            </CardActions>
        </Card>
      </Box>
      <Button
        variant="contained"
        color="primary"
        size="large"
        onClick={this.removeItems}
        >Очичтить
      </Button>
      <Button
        variant="contained"
        color="primary"
        size="large"
        onClick={this.removeItem}
        >Удалить
      </Button>
      </div>
    );
  }
}
