import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import './index.css';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';

const useStyles = makeStyles(theme => ({
  root: {
    width: 1000,
    backgroundColor: theme.palette.background.paper,
  },
}));

export default function WeatherDetails(props) {
  // const [city, setCity] = useState(sessionStorage.getItem("0"));
  const [city, setCity] = useState(props.location.city);
  const [updateTime, setUpdateTime] = useState(null);
  const [weatherData, setWeatherData] = useState({
  "coord": {
    "lon": 0,
    "lat": 0
  },
  "weather": [
    {
      "id": 0,
      "main": "",
      "description": "",
      "icon": ""
    }
  ],
  "base": "",
  "main": {
    "temp": 0,
    "feels_like": 0,
    "temp_min": 0,
    "temp_max": 0,
    "pressure": 0,
    "humidity": 0
  },
  "visibility": 0,
  "wind": {
    "speed": 0,
    "deg": 0
  },
  "clouds": {
    "all": 0
  },
  "dt": 0,
  "sys": {
    "type": 0,
    "id": 0,
    "message": 0,
    "country": "",
    "sunrise": 0,
    "sunset": 0
  },
  "timezone": 0,
  "id": 0,
  "name": "",
  "cod": 0
});
  const classes = useStyles();

  useEffect(() => {
    // if(props.location.city) {
    //   setCity(props.location.city);
    //   sessionStorage.setItem("0", props.location.city);
    // }
    fetchWeather();
  }, []);

  async function fetchWeather() {
    const url = "https://api.openweathermap.org/data/2.5/weather?q=" +
    city + "&lang=ru&units=metric&appid=5fc73283d7afc0b780eee68e8e3bb82b";
    const responce = await fetch(url);
    const data = await responce.json();
    setWeatherData(data);
    setUpdateTime(new Date().toLocaleTimeString());
  }

  return (
    <div>
      <Link to="/" className="Button">Назад</Link>
      <Typography variant="h2">{weatherData.name}</Typography>
      <List component="nav" className={classes.root} aria-label="mailbox folders">
      <ListItem button>
        <ListItemText primary="Inbox" />
      </ListItem>
      <Divider />
      <ListItem button divider>
        <ListItemText primary="Drafts" />
      </ListItem>
      <ListItem button>
        <ListItemText primary="Trash" />
      </ListItem>
      <Divider light />
      <ListItem button>
        <ListItemText primary="Spam" />
      </ListItem>
    </List>
    </div>
  );
}
