import React, {Component} from 'react';
import './index.css';
import WeatherCard from './WeatherCard';
import TextField from '@material-ui/core/TextField';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import Search from './Search';
import UpdateIcon from '@material-ui/icons/Update';

export default class DisplayWeather extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cities: ['Харьков', 'Киев', 'Одесса', 'Львов'],
      buffer: "",
    };
  }

    handleKeyPress = e => {
    if (e.key === "Enter") {
      alert('Enter');
    }
  }

  render() {
    const list = this.state.cities.slice();
    return (
      <div>
      <Box
        display="flex"
        flexWrap="wrap"
        >
        {this.state.cities.map((value, index) => {
        return <Box key = {index} marginRight="50px"><WeatherCard key = {index} name={value} /></Box>
        })}
      </Box>
      <Box
      marginBottom="10px"
      >
      <TextField
        id="outlined-basic"
        label="Поиск по городу"
        variant="filled"
        width="500px"
        onChange={(e) => this.setState({buffer: e.target.value})}
        onKeyPress={this.handleKeyPress}
      />
      </Box>
      <Button
        variant="contained"
        color="primary"
        size="large"
        onClick={() => {
          list.push(this.state.buffer);
          this.setState({cities: list});
        }}
        >Добавить
      </Button>
      <Button
        variant="contained"
        color="primary"
        size="large"
        startIcon={<UpdateIcon />}
        onClick={() => this.setState({state: this.State})}
        >Обновить
      </Button>
      </div>
    );
  }
}
